package ja4

import (
	"encoding/hex"
	"testing"
)

func hexToBytes(t *testing.T, hexClientHello string) []byte {
	t.Helper()
	clientHello, err := hex.DecodeString(hexClientHello)
	if err != nil {
		t.Fatal(err)
	}
	return clientHello
}

func TestFingerprint(t *testing.T) {
	hexClientHello := ""

	fp := JA4Fingerprint{}
	err := fp.UnmarshalBytes(hexToBytes(t, hexClientHello), 't')
	if err != nil {
		t.Error(err)
	}

	str := fp.String()
	if str != "t13d591100_a33745022dd6_a11995863d32" {
		t.Errorf("expected %s, actual %s", "t13d591100_a33745022dd6_a11995863d32", str)
	}
}
